# step 3
def parse_data(result):
    data_query = result # work if only correct status
    events_to_render = []  # to pass to flask
    if data_query['status']:
        site_html = data_query['data']

        soup = BeautifulSoup(site_html, features="html.parser")

        events_lists = soup.select(".event-list--with-advert>.event")


        # could use maps, but for loop are more readable
        for event in events_lists:
            year, details = event.text.split(" ", 1)
            d = dict(year=year, details=details)
            events_to_render.append(d)

    return events_to_render


def main():
    c = get_page_data()
    print(parse_data(c))

from flask import Flask, render_template

app = Flask(__name__)

@app.route('/')
def hello_world():
    page = get_page_data()
    events_in_history = parse_data(page)
    return render_template('index.html', events=events_in_history)

if __name__=="__main__":
    main()